import React from 'react';
import { withRouter } from 'react-router-dom';
import '../appointment/appointment.scss';
import { Input, Table, Button, Modal, Form, Select, message } from 'antd';
const layout = {
  labelCol: { span: 6 },
  wrapperCol: { span: 18 },
};
const success = () => {
  message.success('Add Appointment Successed');
};
class Appointment extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      dataLsit: [],
      visible: false,
      columns: [
        {
          title: 'AppointmentID',
          dataIndex: 'appointmentId',
          key: 'appointmentId',
          render: text => <p>{text}</p>,
        },
        {
          title: 'PatientName',
          dataIndex: 'patientName',
          key: 'patientName',
        },
        {
          title: 'DoctorName',
          dataIndex: 'doctorName',
          key: 'doctorName',
        },
        {
          title: 'Type',
          key: 'type',
          render: (t, r) => {
            if (r.type == 0) {
              return (
                <p>核酸检测</p>
              )
            } else if (r.type == 1) {
              return (
                <p>治疗</p>
              )
            } else {
              return (
                <p>咨询</p>
              )
            }
          }
        },
        {
          title: 'Date',
          key: 'date',
          dataIndex: 'date'
        },
        {
          title: 'Status',
          key: 'status',
          render: (t, r) => {
            if (r.status == 0) {
              return (
                <p>等待</p>
              )
            } else if (r.status == 1) {
              return (
                <p>接收</p>
              )
            } else {
              return (
                <p>取消</p>
              )
            }
          }
        },
        {
          title: 'Setting',
          key: 'setting',
          render: (t, r) => {
              return (
                <div>
                  <Button onClick={this.handleCancle.bind(this, r.appointmentId)}>Confim</Button>
                  <Button onClick={this.handleCancle.bind(this, r.appointmentId)}>Cancle</Button>
                </div>
              )
          }
        },
      ]
    };
    this.getData = this.getData.bind(this)
  }
  componentDidMount() {
    this.getData()
  }
  getData() {
    React.$api.post('/patient/checkApp?patientId=100', {
      // patientId: 100
    }).then(data => {
      console.log(data);
      let c = data.filter((item, index) => {
        item.key = index
        return item
      })
      console.log(c);
      this.setState({
        dataLsit: c
      })
    })
  }
  render() {
    return (
      <div className="appointment">
        <div className="appointment-header">
          <p>New Appointment</p>
          <Input size="large" placeholder="Search" />
        </div>
        <Table columns={this.state.columns} dataSource={this.state.dataLsit} />
      </div>
    );
  }
}

export default withRouter(Appointment);
