import React from 'react';
import { withRouter } from 'react-router-dom';
import './appointment.scss';
import { Input, Table, Button } from 'antd';

class Appointment extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      dataLsit: [],
      columns: [
        {
          title: 'AppointmentID',
          dataIndex: 'appointmentId',
          key: 'appointmentId',
          render: text => <p>{text}</p>,
        },
        {
          title: 'PatientName',
          dataIndex: 'patientName',
          key: 'patientName',
        },
        {
          title: 'DoctorName',
          dataIndex: 'doctorName',
          key: 'doctorName',
        },
        {
          title: 'Type',
          key: 'type',
          render: (t, r) => {
            if(r.type == 0){
              return (
              <p>核酸检测</p>
              )
            }else if(r.type == 1){
              return (
              <p>治疗</p>
              )
            }else{
              return (
                <p>咨询</p>
              )
            }
          }
        },
        {
          title: 'Date',
          key: 'date',
          dataIndex: 'date'
        },
        {
          title: 'Status',
          key: 'status',
          render: (t, r) => {
            if(r.status == 0){
              return (
              <p>等待</p>
              )
            }else if(r.status == 1){
              return (
              <p>接收</p>
              )
            }else{
              return (
                <p>取消</p>
              )
            }
          }
        },
        {
          title: 'Setting',
          key: 'setting',
          render: (t, r) => {
            if(r.status == 0 || r.status == 1){
              return (
                <Button onClick={this.handleCancle.bind(this, r.appointmentId)}>Cancle</Button>
              )
            }
          }
        },
      ]
    };
    this.getData = this.getData.bind(this)
  }
  handleCancle(val){
    console.log(val);
    React.$api.post('/patient/cancel?appointmentId='+val, {
      // appointmentId: val
    }).then(data => {
      console.log(data);
      this.getData()
    })
  }
  componentDidMount(){
    this.getData()
  }
  getData(){
    React.$api.post('/patient/checkApp?patientId=100',{
      // patientId: 100
    }).then(data => {
      console.log(data);
      let c = data.filter((item, index) => {
        item.key = index
        return item
      })
      console.log(c);
      this.setState({
        dataLsit: c
      })
    })
  }
  render() {
    return (
      <div className="appointment">
        <div className="appointment-header">
          <Button type="primary">New Appointment</Button>
          <Input size="large" placeholder="Search"/>
        </div>
        <Table columns={this.state.columns} dataSource={this.state.dataLsit} />
        {/* Appointment */}
      </div>
    );
  }
}

export default withRouter(Appointment);
