import React from 'react';
import { withRouter } from 'react-router-dom';
import './appointment.scss';
import { Input, Table, Button, Modal } from 'antd';
class Appointment extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      dataLsit: [],
      visible: false,
      columns: [
        {
          title: 'AppointmentID',
          dataIndex: 'appointmentId',
          key: 'appointmentId',
          render: text => <p>{text}</p>,
        },
        {
          title: 'PatientName',
          dataIndex: 'patientName',
          key: 'patientName',
        },
        {
          title: 'DoctorName',
          dataIndex: 'doctorName',
          key: 'doctorName',
        },
        {
          title: 'Type',
          key: 'type',
          render: (t, r) => {
            if(r.type == 0){
              return (
              <p>核酸检测</p>
              )
            }else if(r.type == 1){
              return (
              <p>治疗</p>
              )
            }else{
              return (
                <p>咨询</p>
              )
            }
          }
        },
        {
          title: 'Date',
          key: 'date',
          dataIndex: 'date'
        },
        {
          title: 'Status',
          key: 'status',
          render: (t, r) => {
            if(r.status == 0){
              return (
              <p>等待</p>
              )
            }else if(r.status == 1){
              return (
              <p>接收</p>
              )
            }else{
              return (
                <p>取消</p>
              )
            }
          }
        },
        {
          title: 'Setting',
          key: 'setting',
          render: (t, r) => {
            if(r.status == 0 || r.status == 1){
              return (
                <Button onClick={this.handleCancle.bind(this, r.appointmentId)}>Cancle</Button>
              )
            }
          }
        },
      ]
    };
    this.getData = this.getData.bind(this)
    this.showModal = this.showModal.bind(this)
    this.handleOk = this.handleOk.bind(this)
    this.handleCancel = this.handleCancel.bind(this)
  }
  handleCancle(val){
    console.log(val);
    React.$api.post('/patient/cancel?appointmentId='+val, {
      // appointmentId: val
    }).then(data => {
      console.log(data);
      this.getData()
    })
  }
  componentDidMount(){
    this.getData()
  }
  showModal = () => {
    this.setState({
      visible: true,
    });
  };

  handleOk = e => {
    console.log(e);
    this.setState({
      visible: false,
    });
  };

  handleCancel = e => {
    console.log(e);
    this.setState({
      visible: false,
    });
  };
  getData(){
    React.$api.post('/patient/checkApp?patientId=100',{
      // patientId: 100
    }).then(data => {
      console.log(data);
      let c = data.filter((item, index) => {
        item.key = index
        return item
      })
      console.log(c);
      this.setState({
        dataLsit: c
      })
    })
  }
  render() {
    return (
      <div className="appointment">
        <div className="appointment-header">
          <Button type="primary" onClick={this.showModal}>New Appointment</Button>
          <Input size="large" placeholder="Search"/>
        </div>
        <Table columns={this.state.columns} dataSource={this.state.dataLsit} />
        <Modal
          title="Basic Modal"
          visible={this.state.visible}
          onOk={this.handleOk}
          onCancel={this.handleCancel}
        >
          <Input placeholder="Basic usage" />
          <Input placeholder="Basic usage" />
          <Input placeholder="Basic usage" />
          <Input placeholder="Basic usage" />
        </Modal>
        {/* Appointment */}
      </div>
    );
  }
}

export default withRouter(Appointment);
