import React from 'react';
import { HashRouter as Router, withRouter } from 'react-router-dom';
import './doctor.scss';
import { Table, Button, Input, Modal, Select } from 'antd';
const { Option } = Select;
// function 
class Doctor extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      dataSource:[
        {
          key: '1',
          name: '胡彦斌',
          age: 32,
          address: '西湖区湖底公园1号',
        },
        {
          key: '2',
          name: '胡彦祖',
          age: 42,
          address: '西湖区湖底公园1号',
        },
      ],
      columns:[
        {
          title: 'Name',
          dataIndex: 'name',
          key: 'name',
        },
        {
          title: 'Gender',
          dataIndex: 'gender',
          key: 'gender',
        },
        {
          title: 'Age',
          dataIndex: 'age',
          key: 'age',
        },
        {
          title: 'Phone',
          dataIndex: 'phone',
          key: 'phone',
        },
        {
          title: 'Address',
          dataIndex: 'address',
          key: 'address',
        },
        {
          title: 'Status',
          dataIndex: 'status',
          key: 'status',
          render: (t, r) => {
            if (r.status == 1) {
              return (
                <p>阳性</p>
              )
            } else if (r.type == 2) {
              return (
                <p>良性</p>
              )
            } else {
              return (
                <p>隔离</p>
              )
            }
          }
        },
        {
          title: 'Setting',
          key:"setting",
          render: (t, r) => {
              return (
                <Button type="link" onClick={this.showModal.bind(this, r)}>Detail</Button>
              )
          }
        }
      ],
      patientId:"",
      doctorId:"",
      name:"",
      selects:"",
      visible: false
    };
    this.getData = this.getData.bind(this)
    this.handleAddPatient = this.handleAddPatient.bind(this)
    this.handleDeletePatient = this.handleDeletePatient.bind(this)
    this.changeone = this.changeone.bind(this)
    this.changetwo = this.changetwo.bind(this)
    this.handleChange = this.handleChange.bind(this)
  }
  componentDidMount(){
    this.getData()
  }
  handleChange(value) {
    this.setState({
      selects: value
    })
    // console.log(`selected ${value}`);
  }
  getData(){
    React.$api.post('/doctor/checkPatient?doctorId='+window.localStorage.getItem('id')).then(data => {
      console.log(data);
      this.setState({
        dataSource: data
      })
    })
  }
  handleAddPatient(){
    console.log('add');
    React.$api.post(`/doctor/addPatient?patientId=${this.state.patientId}&doctorId=${this.state.doctorId}`).then(data => {
      console.log(data);
      this.getData()
    })
  }
  handleDeletePatient(){
    React.$api.post(`/doctor/deletePatient?patientId=${this.state.patientId}&doctorId=${this.state.doctorId}`).then(data => {
      console.log(data);
      this.getData()
    })
  }
  changeone(e){
    this.setState({
      patientId: e.target.value
    })
  }
  changetwo(e){
    this.setState({
      doctorId: e.target.value
    })
  }
  showModal = (p) => {
    React.$api.post('/doctor/checkDetail?patientId=100').then(data => {
      console.log(data[p.name]);
      this.setState({
        visible: true,
        name: p.name,
        selects: data[p.name],
        pState: data
      });
    })
    console.log(p);
  };

  handleOk = e => {
    console.log(e);
    let _select = this.state.pState
    _select.status = this.state.selects
    React.$api.post('/doctor/editDetail',{
      state:_select
    }).then(data => {
      console.log(data);
    })
    this.setState({
      visible: false,
    });
  };

  handleCancel = e => {
    console.log(e);
    this.setState({
      visible: false,
    });
  };
  render() {
    return (
      <div className="doctor">
        <Input onChange={this.changeone} placeholder="PatientId *" value={this.state.patientId}/>
        <Input onChange={this.changetwo} placeholder="DoctorId *" value={this.state.doctorId}/>
        <Button onClick={this.handleAddPatient}>Add</Button>
        <Button onClick={this.handleDeletePatient}>Delete</Button>
        <Table columns={this.state.columns} bordered dataSource={this.state.dataSource} />
        <Modal
          title="Basic Modal"
          visible={this.state.visible}
          onOk={this.handleOk}
          onCancel={this.handleCancel}
        >
          <div style={{ margin: '0 0 20px 0' }}>
          Name:<Input style={{ width: 250}} disabled value={this.state.name}/>
          </div>
          <div>
          Status:
          <Select value={this.state.selects} style={{ width: 250 }} onChange={this.handleChange}>
          <Option value="1">阳性</Option>
          <Option value="2">良性</Option>
          <Option value="0">隔离</Option>
        </Select>
        </div>
        </Modal>
      </div>
    );
  }
}

export default withRouter(Doctor);
