import React from 'react';
import { withRouter } from 'react-router-dom';
import './appointment.scss';
import { Input, Table, Button } from 'antd';

class Appointment extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      dataLsit: [],
      columns: [
        {
          title: 'AppointmentID',
          dataIndex: 'appointmentId',
          key: 'appointmentId',
          render: text => <p>{text}</p>,
        },
        {
          title: 'PatientName',
          dataIndex: 'patientName',
          key: 'patientName',
        },
        {
          title: 'DoctorName',
          dataIndex: 'doctorName',
          key: 'doctorName',
        },
        {
          title: 'Type',
          key: 'type',
          dataIndex: 'type'
        },
        {
          title: 'Date',
          key: 'date',
          dataIndex: 'date'
        },
        {
          title: 'Status',
          key: 'status',
          dataIndex: 'status'
        },
        {
          title: 'Setting',
          key: 'setting',
          render: (t, r) => {
            if(r.status == 0 || r.status == 1){
              return (
                <Button onClick={this.handleCancle.bind(this, r.appointmentId)}>Cancle</Button>
              )
            }
          }
        },
      ]
    };
  }
  handleCancle(val){
    console.log(val);
    React.$api.post('/patient/cancle')
  }
  componentDidMount(){
    React.$api.post('/patient/checkApp?patientId=100',{
      // patientId: 100
    }).then(data => {
      console.log(data);
      let c = data.filter((item, index) => {
        item.key = index
        return item
      })
      console.log(c);
      this.setState({
        dataLsit: c
      })
    })
  }
  render() {
    return (
      <div className="appointment">
        <div className="appointment-header">
          <p>Appointment</p>
          <Input size="large" placeholder="Search"/>
        </div>
        <Table columns={this.state.columns} dataSource={this.state.dataLsit} />
        {/* Appointment */}
      </div>
    );
  }
}

export default withRouter(Appointment);
